mapper.select:
SELECT

from leavess
where
id = #{leaveId}
and application = #{userId}

mapper.update:
UPDATE leavess
set status = #{status}
where id = #{leaveId}

mapper.select:
SELECT

from leavess
where
application = #{userId}
and status = #{status}

SELECT

from leavess
where application = #{userId}
and status != #{status}
